import axios from "axios";
import Header from "./Header";
import Footer from "./Footer";

export default {
  data() {
    return {
      // image file name
      image: null,
      activationMap: [],
      activationColor: [255, 0, 0, 100],
      inactivationColor: [0, 0, 0, 0],
      tool_started: false,
      tool: "deactivate",
      tool_size: 10,
      canvas_width: 436,
      canvas_height: 364,
      map_id: 0
    };
  },
  methods: {
    getActivationMap: function() {
      const path = "http://localhost:5000/active_learning";
      axios
        .get(path)
        .then(res => {
          this.image = res.data.model_output.image;
          this.activationMap = res.data.model_output.activation;
          console.log(this.activationMap)
          this.drawImage();
        })
        .catch(error => {
          console.error(error);
        });
    },
    drawImage: function() {
      // load brain image and activation map onto canvas
      let img_ctx = this.$refs.img.getContext("2d");
      // set global opacity
      img_ctx.globalAlpha = 1.0;
      // draw brain scan
      let img = new Image();
      img.src = require("/Users/shawnfan/active_learning_prototype/" +
        this.image);
      img.onload = () => {
        img_ctx.drawImage(img, 0, 0);
      };
    },
    drawActivation: function() {
      // draw activation map
      let map_ctx = this.$refs.map.getContext("2d");
      map_ctx.globalAlpha = 0;
      // create blank imageData - transparent black pixels (0,0,0,0)
      let activation_map = map_ctx.createImageData(
        this.canvas_width,
        this.canvas_height
      );
      for (let i = 0; i < activation_map.height; i++) {
        for (let k = 0; k < activation_map.width; k++) {
          let map_index = 4 * (activation_map.width * i + k);
          console.log(this.activationMap)
          if (this.activationMap[i][k] === 1) {
            //this.activationMap.activation – a list of binary lists: 1 - activated, 0 - inactivated
            // (255, 0, 0) = red
            activation_map.data[map_index] = this.activationColor[0];
            activation_map.data[map_index + 1] = this.activationColor[1];
            activation_map.data[map_index + 2] = this.activationColor[2];
            activation_map.data[map_index + 3] = this.activationColor[3];
          } else {
            // (0, 0, 0) = black
            activation_map.data[map_index] = this.inactivationColor[0];
            activation_map.data[map_index + 1] = this.inactivationColor[1];
            activation_map.data[map_index + 2] = this.inactivationColor[2];
            activation_map.data[map_index + 3] = this.inactivationColor[3];
          }
        }
      }
      map_ctx.putImageData(activation_map, 0, 0);
    },
    drawCursor: function(x, y) {
      let ctx = this.$refs.cursor.getContext("2d");
      ctx.clearRect(0, 0, this.canvas_width, this.canvas_height);
      ctx.fillStyle = "rgba(255,255,255,255)";
      ctx.beginPath();
      ctx.arc(x, y, this.tool_size / 2, 0, 2 * Math.PI);
      ctx.fill();
    },
    correctActivation: function(event) {
      // draw cursor
      this.drawCursor(event.offsetX, event.offsetY);
      // highlight incorrect activation
      let canvas = this.$refs.draw;
      canvas.globalAlpha = 0.5;
      let ctx = this.$refs.draw.getContext("2d");
      if (this.tool === "deactivate") {
        ctx.strokeStyle = "rgba(0, 0, 255, 255)";
        ctx.fillStyle = "rgba(0, 0, 255, 255)";
        ctx.globalCompositeOperation = "source-over";
      } else if (this.tool === "activate") {
        ctx.strokeStyle = "rgba(255, 0, 0, 255)";
        ctx.fillStyle = "rgba(255, 0, 0, 255)";
        ctx.globalCompositeOperation = "source-over";
      }
      ctx.lineJoin = "round";
      ctx.lineWidth = this.tool_size;
      canvas.onmousedown = () => {
        // draw circle
        ctx.beginPath();
        ctx.arc(
          event.offsetX,
          event.offsetY,
          this.tool_size / 2,
          0,
          2 * Math.PI
        );
        ctx.fill();
        this.updateActivationMap();
        // start drawing
        this.tool_started = true;
        ctx.beginPath();
        ctx.moveTo(event.offsetX, event.offsetY);
      };
      if (this.tool_started) {
        ctx.lineTo(event.offsetX, event.offsetY);
        ctx.stroke();
        this.updateActivationMap();
      }
      canvas.onmouseup = () => {
        // stop drawing
        ctx.beginPath();
        ctx.arc(
          event.offsetX,
          event.offsetY,
          this.tool_size / 2,
          0,
          2 * Math.PI
        );
        ctx.fill();
        this.updateActivationMap();
        if (this.tool_started) {
          this.tool_started = false;
        }
      };
    },
    setTool: function(tool) {
      this.tool = tool;
    },
    setToolSize: function(tool_size) {
      this.tool_size = tool_size;
    },
    updateActivationMap: function() {
      let map_ctx = this.$refs.map.getContext("2d");
      let draw_ctx = this.$refs.draw.getContext("2d");
      let map_data = map_ctx.getImageData(
        0,
        0,
        this.canvas_width,
        this.canvas_height
      );
      let correction_data = draw_ctx.getImageData(
        0,
        0,
        this.canvas_width,
        this.canvas_height
      );

      let index = 0;
      // activation pixel color
      let pixel = this.activationColor;
      if (this.tool === "deactivate") {
        index = 2;
        // deactivation pixel value
        pixel = this.inactivationColor;
      }
      for (let i = 0; i < correction_data.data.length; i += 4) {
        if (correction_data.data[i + index] === 255) {
          map_data.data[i] = pixel[0];
          map_data.data[i + 1] = pixel[1];
          map_data.data[i + 2] = pixel[2];
          map_data.data[i + 3] = pixel[3];
        }
      }
      // clear drawing
      let clear_data = map_ctx.createImageData(map_data);
      draw_ctx.putImageData(clear_data, 0, 0);
      // draw updated activation map
      map_ctx.putImageData(map_data, 0, 0);
    },
    saveData: function(payload) {
      const path = "http://localhost:5000/active_learning";
      axios
        .post(path, payload)
        .then(() => {
          alert("Progress saved!");
          this.getActivationMap();
        })
        .catch(error => {
          console.log(error);
        });
    },
    onSubmit: function(event) {
      this.updateActivationMap();
      let map_ctx = this.$refs.map.getContext("2d");
      let corrected_map = map_ctx.getImageData(
        0,
        0,
        this.canvas_width,
        this.canvas_height
      );
      const payload = {
        corrected_activation: corrected_map.data,
        map_id: this.map_id
      };
      this.saveData(payload);
      this.map_id = this.map_id + 1;
    }
  },
  components: {
    "app-header": Header,
    "app-footer": Footer
  },
  created() {
    this.getActivationMap();
  },
  mounted() {}
};
